  // Payment and Order routes
  app.post("/api/create-payment-intent", async (req, res) => {
    try {
      let cart;

      if (req.isAuthenticated()) {
        [cart] = await db.select().from(carts).where(eq(carts.userId, req.user!.id));
      } else if (req.session.cartId) {
        [cart] = await db.select().from(carts).where(eq(carts.id, req.session.cartId));
      }

      if (!cart) {
        return res.status(400).json({ error: "Cart not found" });
      }

      const items = await db
        .select({
          id: cartItems.id,
          quantity: cartItems.quantity,
          product: products,
        })
        .from(cartItems)
        .innerJoin(products, eq(cartItems.productId, products.id))
        .where(eq(cartItems.cartId, cart.id));

      if (items.length === 0) {
        return res.status(400).json({ error: "Cart is empty" });
      }

      let total = 0;
      items.forEach(item => {
        total += parseFloat(item.product.price) * item.quantity;
      });

      const amountInFils = Math.round(total * 100);

      if (!stripe) {
        return res.status(503).json({ error: "Payment service not configured. Please contact administrator." });
      }

      const paymentIntent = await stripe.paymentIntents.create({
        amount: amountInFils,
        currency: "aed",
        automatic_payment_methods: {
          enabled: true,
        },
        metadata: {
          cartId: cart.id,
        },
      });

      res.json({ clientSecret: paymentIntent.client_secret });
    } catch (error) {
      console.error("Error creating payment intent:", error);
      res.status(500).json({ error: "Failed to create payment intent" });
    }
  });

  app.post("/api/orders", async (req, res) => {
    try {
      const { paymentIntentId, shippingAddress, customerName, customerEmail, customerPhone } = req.body;

      if (!paymentIntentId || !shippingAddress || !customerName) {
        return res.status(400).json({ error: "Missing required fields" });
      }

      if (!stripe) {
        return res.status(503).json({ error: "Payment service not configured" });
      }

      const paymentIntent = await stripe.paymentIntents.retrieve(paymentIntentId);

      if (paymentIntent.status !== "succeeded") {
        return res.status(400).json({ error: "Payment not completed" });
      }

      let cart;
      let userId = null;

      const cartIdFromPayment = paymentIntent.metadata?.cartId;

      if (req.isAuthenticated()) {
        userId = req.user!.id;
        [cart] = await db.select().from(carts).where(eq(carts.userId, userId));
      } else if (cartIdFromPayment) {
        [cart] = await db.select().from(carts).where(eq(carts.id, cartIdFromPayment));
      } else if (req.session.cartId) {
        [cart] = await db.select().from(carts).where(eq(carts.id, req.session.cartId));
      }

      if (!cart) {
        return res.status(400).json({ error: "Cart not found" });
      }

      const items = await db
        .select({
          id: cartItems.id,
          quantity: cartItems.quantity,
          selectedColor: cartItems.selectedColor,
          selectedSize: cartItems.selectedSize,
          product: products,
        })
        .from(cartItems)
        .innerJoin(products, eq(cartItems.productId, products.id))
        .where(eq(cartItems.cartId, cart.id));

      if (items.length === 0) {
        return res.status(400).json({ error: "Cart is empty" });
      }

      let total = 0;
      items.forEach(item => {
        total += parseFloat(item.product.price) * item.quantity;
      });

      const [order] = await db
        .insert(orders)
        .values({
          userId: userId ?? null,
          status: "pending",
          total: total.toFixed(2),
          discountAmount: "0",
          customerName,
          customerEmail: customerEmail || null,
          customerPhone: customerPhone || null,
          shippingAddress,
          paymentMethod: "stripe",
          paymentStatus: "completed",
          transactionId: paymentIntentId,
        })
        .returning();

      const orderItemsData = items.map(item => ({
        orderId: order.id,
        productId: item.product.id,
        productName: item.product.nameEn,
        quantity: item.quantity,
        price: item.product.price,
        selectedColor: item.selectedColor,
        selectedSize: item.selectedSize,
      }));

      await db.insert(orderItems).values(orderItemsData);

      await db.delete(cartItems).where(eq(cartItems.cartId, cart.id));

      res.status(201).json(order);
    } catch (error) {
      console.error("Error creating order:", error);
      res.status(500).json({ error: "Failed to create order" });
    }
  });

