          and(
            eq(discountCodes.code, code.toUpperCase()),
            sql`${discountCodes.id} != ${req.params.id}`
          )
        );

      if (existing) {
        return res.status(400).json({ error: "Discount code already exists" });
      }

      const [updatedDiscount] = await db
        .update(discountCodes)
        .set({
          code: code.toUpperCase(),
          type,
          value,
          minPurchase: minPurchase || null,
          isActive: isActive ?? true,
          expiresAt: expiresAt || null,
          bundleProducts: bundleProducts || null,
        })
        .where(eq(discountCodes.id, req.params.id))
        .returning();

      if (!updatedDiscount) {
        return res.status(404).json({ error: "Discount code not found" });
      }

      res.json(updatedDiscount);
    } catch (error) {
      console.error("Error updating discount code:", error);
      res.status(500).json({ error: "Failed to update discount code" });
    }
  });

  app.delete("/api/admin/discounts/:id", requireAdmin, async (req, res) => {
    try {
      await db.delete(discountCodes).where(eq(discountCodes.id, req.params.id));
      res.sendStatus(204);
    } catch (error) {
      console.error("Error deleting discount code:", error);
      res.status(500).json({ error: "Failed to delete discount code" });
    }
  });

  // Stripe payment routes
  app.post("/api/create-payment-intent", async (req, res) => {
    if (!stripe) {
      return res.status(500).json({ error: "Stripe is not configured" });
    }

    try {
      const { amount } = req.body;

      if (!amount || amount <= 0) {
        return res.status(400).json({ error: "Invalid amount" });
      }

      const paymentIntent = await stripe.paymentIntents.create({
        amount: Math.round(amount * 100),
        currency: "aed",
      });

      res.json({ clientSecret: paymentIntent.client_secret });
    } catch (error: any) {
      console.error("Error creating payment intent:", error);
      res.status(500).json({ error: "Error creating payment intent: " + error.message });
    }
  });

  // Order creation endpoint
  app.post("/api/orders", async (req, res) => {
    try {
      const { items: cartItemsData, total, customerName, customerEmail, customerPhone, shippingAddress, transactionId } = req.body;

      if (!cartItemsData || cartItemsData.length === 0) {
        return res.status(400).json({ error: "Cart is empty" });
      }

      if (!customerName || !shippingAddress) {
        return res.status(400).json({ error: "Missing required customer information" });
      }

      const userId = req.isAuthenticated() ? req.user!.id : null;

      const [order] = await db.insert(orders).values({
        userId: userId!,
        status: "pending",
        total: total.toString(),
        discountAmount: "0",
        customerName,
        customerEmail: customerEmail || null,
        customerPhone: customerPhone || null,
        shippingAddress,
        paymentMethod: "stripe",
        paymentStatus: "completed",
        transactionId: transactionId || null,
      }).returning();

      for (const item of cartItemsData) {
        await db.insert(orderItems).values({
          orderId: order.id,
          productId: item.product.id,
          productName: item.product.nameEn,
          quantity: item.quantity,
          price: item.product.price,
          selectedColor: item.selectedColor || null,
          selectedSize: item.selectedSize || null,
        });
      }

      await db.insert(orderStatusHistory).values({
        orderId: order.id,
        status: "pending",
        note: "Order created",
      });

      res.status(201).json(order);
    } catch (error) {
      console.error("Error creating order:", error);
      res.status(500).json({ error: "Failed to create order" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

